INSERT INTO lessons (unit_id, title, arabic_text, transliteration, order_index) VALUES
  ('11010101-0101-0101-0101-010101010101', 'السلام عليكم', 'السَّلامُ عَلَيكُم وَرَحمَةُ اللهِ وَبَرَكاتُه', 'As-salāmu ʿalaykum wa raḥmatu Allāhi wa barakātuh', 1),
  ('11010101-0101-0101-0101-010101010101', 'الرد على السلام', 'وَعَلَيكُمُ السَّلامُ وَرَحمَةُ اللهِ وَبَرَكاتُه', 'Wa ʿalaykumu as-salāmu wa raḥmatu Allāhi wa barakātuh', 2),
  ('11010101-0101-0101-0101-010101010101', 'كيف حالك؟', 'كَيفَ حالُكَ؟ أنا بِخَيرٍ، الحَمدُ للهِ', 'Kayfa ḥāluka? Ana bi-khayrin, al-ḥamdu lillāh', 3),
  ('11010101-0101-0101-0101-010101010101', 'ما اسمك؟', 'ما اسمُكَ؟ اسمي أحمَد. تَشَرَّفنا', 'Mā ismuka? Ismī Aḥmad. Tasharrafnā', 4),
  ('11010101-0101-0101-0101-010101010101', 'من أين أنت؟', 'مِن أينَ أنتَ؟ أنا مِن مِصر', 'Min ayna anta? Ana min Miṣr', 5),
  ('11010101-0101-0101-0101-010101010101', 'أهلاً وسهلاً', 'أهلاً وَسَهلاً بِكَ. مَرحَباً', 'Ahlan wa sahlan bika. Marḥabā', 6),
  ('11010101-0101-0101-0101-010101010101', 'صباح الخير', 'صَباحُ الخَيرِ. صَباحُ النُّورِ', 'Ṣabāḥu al-khayri. Ṣabāḥu an-nūr', 7),
  ('11010101-0101-0101-0101-010101010101', 'مساء الخير', 'مَساءُ الخَيرِ. مَساءُ النُّورِ', 'Masāʾu al-khayri. Masāʾu an-nūr', 8),
  ('11010101-0101-0101-0101-010101010101', 'تشرفت بمعرفتك', 'تَشَرَّفتُ بِمَعرِفَتِكَ. سَعيدٌ بِلِقائِكَ', 'Tasharraftu bi-maʿrifatika. Saʿīdun bi-liqāʾika', 9),
  ('11010101-0101-0101-0101-010101010101', 'أين تسكن؟', 'أينَ تَسكُنُ؟ أسكُنُ في القاهِرَة', 'Ayna taskunu? Askunu fī al-Qāhira', 10),
  ('11010101-0101-0101-0101-010101010101', 'ما عملك؟', 'ما عَمَلُكَ؟ أنا طَبيبٌ / مُهَندِسٌ / مُعَلِّمٌ', 'Mā ʿamaluka? Ana ṭabībun / muhandisun / muʿallimun', 11),
  ('11010101-0101-0101-0101-010101010101', 'كم عمرك؟', 'كَم عُمرُكَ؟ عُمري ثَلاثونَ سَنَة', 'Kam ʿumruka? ʿUmrī thalāthūna sana', 12),
  ('11010101-0101-0101-0101-010101010101', 'هل أنت متزوج؟', 'هَل أنتَ مُتَزَوِّجٌ؟ نَعَم / لا، أنا أعزَب', 'Hal anta mutazawwijun? Naʿam / Lā, ana aʿzab', 13),
  ('11010101-0101-0101-0101-010101010101', 'عفواً واعتذار', 'عَفواً. أعتَذِرُ. لا بَأسَ عَلَيكَ', 'ʿAfwan. Aʿtadhiru. Lā baʾsa ʿalayka', 14),
  ('11010101-0101-0101-0101-010101010101', 'الوداع', 'إلى اللِّقاءِ. مَعَ السَّلامَة. في أمانِ اللهِ', 'Ilā al-liqāʾ. Maʿa as-salāma. Fī amāni Allāh', 15);
INSERT INTO lessons (unit_id, title, arabic_text, transliteration, order_index) VALUES
  ('11020202-0202-0202-0202-020202020202', 'الألف والباء', 'أَلِف - باء. أَسَد، بَيت', 'Alif - Bāʾ. Asad, Bayt', 1),
  ('11020202-0202-0202-0202-020202020202', 'التاء والثاء', 'تاء - ثاء. تُفّاحَة، ثَعلَب', 'Tāʾ - Thāʾ. Tuffāḥa, Thaʿlab', 2),
  ('11020202-0202-0202-0202-020202020202', 'الجيم والحاء', 'جيم - حاء. جَمَل، حِصان', 'Jīm - Ḥāʾ. Jamal, Ḥiṣān', 3),
  ('11020202-0202-0202-0202-020202020202', 'الخاء والدال', 'خاء - دال. خُبز، دَجاجَة', 'Khāʾ - Dāl. Khubz, Dajāja', 4),
  ('11020202-0202-0202-0202-020202020202', 'الذال والراء', 'ذال - راء. ذِئب، رُمّان', 'Dhāl - Rāʾ. Dhiʾb, Rummān', 5),
  ('11020202-0202-0202-0202-020202020202', 'الزاي والسين', 'زاي - سين. زَهرَة، سَمَكَة', 'Zāy - Sīn. Zahra, Samaka', 6),
  ('11020202-0202-0202-0202-020202020202', 'الشين والصاد', 'شين - صاد. شَمس، صَقر', 'Shīn - Ṣād. Shams, Ṣaqr', 7),
  ('11020202-0202-0202-0202-020202020202', 'الضاد والطاء', 'ضاد - طاء. ضِفدَع، طائِر', 'Ḍād - Ṭāʾ. Ḍifdaʿ, Ṭāʾir', 8),
  ('11020202-0202-0202-0202-020202020202', 'الظاء والعين', 'ظاء - عين. ظَبي، عِنَب', 'Ẓāʾ - ʿAyn. Ẓaby, ʿInab', 9),
  ('11020202-0202-0202-0202-020202020202', 'الغين والفاء', 'غين - فاء. غَزال، فَراشَة', 'Ghayn - Fāʾ. Ghazāl, Farāsha', 10),
  ('11020202-0202-0202-0202-020202020202', 'القاف والكاف', 'قاف - كاف. قَمَر، كِتاب', 'Qāf - Kāf. Qamar, Kitāb', 11),
  ('11020202-0202-0202-0202-020202020202', 'اللام والميم', 'لام - ميم. لَيمون، مَوز', 'Lām - Mīm. Laymūn, Mawz', 12),
  ('11020202-0202-0202-0202-020202020202', 'النون والهاء', 'نون - هاء. نَجمَة، هِلال', 'Nūn - Hāʾ. Najma, Hilāl', 13),
  ('11020202-0202-0202-0202-020202020202', 'الواو والياء', 'واو - ياء. وَردَة، يَد', 'Wāw - Yāʾ. Warda, Yad', 14),
  ('11020202-0202-0202-0202-020202020202', 'الحركات والتشكيل', 'الفَتحَة وَالكَسرَة وَالضَّمَّة وَالسُّكون', 'Al-fatḥa wa al-kasra wa aḍ-ḍamma wa as-sukūn', 15);
INSERT INTO lessons (unit_id, title, arabic_text, transliteration, order_index) VALUES
  ('11030303-0303-0303-0303-030303030303', 'الأرقام 1-5', 'واحِد، اثنان، ثَلاثَة، أربَعَة، خَمسَة', 'Wāḥid, ithnān, thalātha, arbaʿa, khamsa', 1),
  ('11030303-0303-0303-0303-030303030303', 'الأرقام 6-10', 'سِتَّة، سَبعَة، ثَمانِيَة، تِسعَة، عَشَرَة', 'Sitta, sabʿa, thamāniya, tisʿa, ʿashara', 2),
  ('11030303-0303-0303-0303-030303030303', 'الأرقام 11-15', 'أحَدَ عَشَر، اثنا عَشَر، ثَلاثَةَ عَشَر، أربَعَةَ عَشَر، خَمسَةَ عَشَر', 'Aḥada ʿashar, ithnā ʿashar, thalāthata ʿashar, arbaʿata ʿashar, khamsata ʿashar', 3),
  ('11030303-0303-0303-0303-030303030303', 'الأرقام 16-20', 'سِتَّةَ عَشَر، سَبعَةَ عَشَر، ثَمانِيَةَ عَشَر، تِسعَةَ عَشَر، عِشرون', 'Sittata ʿashar, sabʿata ʿashar, thamāniyata ʿashar, tisʿata ʿashar, ʿishrūn', 4),
  ('11030303-0303-0303-0303-030303030303', 'العشرات', 'عِشرون، ثَلاثون، أربَعون، خَمسون', 'ʿIshrūn, thalāthūn, arbaʿūn, khamsūn', 5),
  ('11030303-0303-0303-0303-030303030303', 'المزيد من العشرات', 'سِتّون، سَبعون، ثَمانون، تِسعون', 'Sittūn, sabʿūn, thamānūn, tisʿūn', 6),
  ('11030303-0303-0303-0303-030303030303', 'المئة والألف', 'مِئَة، مِئَتان، ألف، ألفان', 'Miʾa, miʾatān, alf, alfān', 7),
  ('11030303-0303-0303-0303-030303030303', 'الأرقام المركبة', 'واحِدٌ وَعِشرون، اثنانِ وَثَلاثون، خَمسَةٌ وَأربَعون', 'Wāḥidun wa ʿishrūn, ithnāni wa thalāthūn, khamsatun wa arbaʿūn', 8),
  ('11030303-0303-0303-0303-030303030303', 'الأرقام الترتيبية الأولى', 'الأوَّل، الثاني، الثالِث، الرابِع، الخامِس', 'Al-awwal, ath-thānī, ath-thālith, ar-rābiʿ, al-khāmis', 9),
  ('11030303-0303-0303-0303-030303030303', 'الأرقام الترتيبية التالية', 'السادِس، السابِع، الثامِن، التاسِع، العاشِر', 'As-sādis, as-sābiʿ, ath-thāmin, at-tāsiʿ, al-ʿāshir', 10),
  ('11030303-0303-0303-0303-030303030303', 'كم الثمن؟', 'كَم الثَّمَن؟ الثَّمَنُ عَشَرَةُ دَولاراتٍ', 'Kam ath-thaman? Ath-thamanu ʿasharatu dūlārāt', 11),
  ('11030303-0303-0303-0303-030303030303', 'العمليات الحسابية', 'واحِدٌ زائِدٌ واحِدٌ يُساوي اثنَين', 'Wāḥidun zāʾidun wāḥidun yusāwī ithnayni', 12),
  ('11030303-0303-0303-0303-030303030303', 'الضرب والقسمة', 'ثَلاثَةٌ ضَرب أربَعَة يُساوي اثنَي عَشَر', 'Thalāthatun ḍarb arbaʿa yusāwī ithnay ʿashar', 13),
  ('11030303-0303-0303-0303-030303030303', 'الساعة بالأرقام', 'الساعَةُ الثالِثَة. السَاعَةُ الخامِسَة وَالنِّصف', 'As-sāʿatu ath-thālitha. As-sāʿatu al-khāmisa wan-niṣf', 14),
  ('11030303-0303-0303-0303-030303030303', 'التاريخ بالأرقام', 'اليَومُ الخامِس مِن شَهرِ يَنايِر', 'Al-yawmu al-khāmis min shahri yanāyir', 15);