INSERT INTO lessons (unit_id, title, arabic_text, transliteration, order_index) VALUES
  ('11040404-0404-0404-0404-040404040404', 'الأب والأم', 'هذا أبي وَهذِهِ أُمّي. أُحِبُّهُما كَثيراً', 'Hādhā abī wa hādhihi ummī. Uḥibbuhuma kathīrā', 1),
  ('11040404-0404-0404-0404-040404040404', 'الأخ والأخت', 'عِندي أَخٌ وَأُختٌ. أَخي أكبَرُ مِنّي', 'ʿIndī akhun wa ukhtun. Akhī akbaru minnī', 2),
  ('11040404-0404-0404-0404-040404040404', 'الجد والجدة', 'جَدّي وَجَدَّتي يَعيشانِ في الرِّيف', 'Jaddī wa jaddatī yaʿīshāni fī ar-rīf', 3),
  ('11040404-0404-0404-0404-040404040404', 'العم والعمة', 'عَمّي طَبيبٌ وَعَمَّتي مُعَلِّمَة', 'ʿAmmī ṭabībun wa ʿammatī muʿallima', 4),
  ('11040404-0404-0404-0404-040404040404', 'الخال والخالة', 'خالي يَسكُنُ في المَدينَة. خالَتي لَطيفَةٌ جِدّاً', 'Khālī yaskunu fī al-madīna. Khālatī laṭīfatun jiddan', 5),
  ('11040404-0404-0404-0404-040404040404', 'الابن والابنة', 'عِندي ابنٌ وَابنَةٌ. ابني اسمُهُ يوسُف', 'ʿIndī ibnun wa ibnatun. Ibnī ismuhu Yūsuf', 6),
  ('11040404-0404-0404-0404-040404040404', 'الزوج والزوجة', 'هذا زَوجي. هذِهِ زَوجَتي. نَحنُ مُتَزَوِّجان', 'Hādhā zawjī. Hādhihi zawjatī. Naḥnu mutazawwijān', 7),
  ('11040404-0404-0404-0404-040404040404', 'ابن العم وابنة العم', 'ابنُ عَمّي صَديقي. ابنَةُ عَمّي طالِبَةٌ', 'Ibnu ʿammī ṣadīqī. Ibnatu ʿammī ṭāliba', 8),
  ('11040404-0404-0404-0404-040404040404', 'الحفيد والحفيدة', 'جَدّي لَدَيهِ خَمسَةُ أحفاد', 'Jaddī ladayhi khamsatu aḥfād', 9),
  ('11040404-0404-0404-0404-040404040404', 'العائلة الكبيرة', 'عائِلَتُنا كَبيرَةٌ. نَجتَمِعُ في الأعياد', 'ʿĀʾilatunā kabīra. Najtamiʿu fī al-aʿyād', 10),
  ('11040404-0404-0404-0404-040404040404', 'صفات أفراد العائلة', 'أبي كَريمٌ. أُمّي حَنونَةٌ. جَدّي حَكيمٌ', 'Abī karīmun. Ummī ḥanūna. Jaddī ḥakīm', 11),
  ('11040404-0404-0404-0404-040404040404', 'أعمار العائلة', 'أبي في الخَمسين. أُمّي أصغَرُ مِنهُ بِسَنَتَين', 'Abī fī al-khamsīn. Ummī aṣgharu minhu bi-sanatayn', 12),
  ('11040404-0404-0404-0404-040404040404', 'مهن العائلة', 'أبي يَعمَلُ في شَرِكَة. أُمّي رَبَّةُ بَيت', 'Abī yaʿmalu fī sharika. Ummī rabbatu bayt', 13),
  ('11040404-0404-0404-0404-040404040404', 'المناسبات العائلية', 'نَحتَفِلُ بِعيدِ ميلادِ جَدَّتي اليَوم', 'Naḥtafilu bi-ʿīdi mīlādi jaddatī al-yawm', 14),
  ('11040404-0404-0404-0404-040404040404', 'الدعاء للعائلة', 'رَبِّ ارحَمهُما كَما رَبَّياني صَغيراً', 'Rabbi irḥamhumā kamā rabbayānī ṣaghīrā', 15);
INSERT INTO lessons (unit_id, title, arabic_text, transliteration, order_index) VALUES
  ('11050505-0505-0505-0505-050505050505', 'أنواع الخبز', 'خُبزٌ، رَغيفٌ، خُبزُ الشَّعير، الخُبزُ الأسمَر', 'Khubzun, raghīfun, khubzu ash-shaʿīr, al-khubzu al-asmar', 1),
  ('11050505-0505-0505-0505-050505050505', 'الفواكه', 'تُفّاحٌ، مَوزٌ، بُرتُقالٌ، عِنَبٌ، فَراوِلَة', 'Tuffāḥ, mawz, burtuqāl, ʿinab, farāwila', 2),
  ('11050505-0505-0505-0505-050505050505', 'الخضروات', 'طَماطِم، خِيار، بَصَل، ثُوم، جَزَر', 'Ṭamāṭim, khiyār, baṣal, thūm, jazar', 3),
  ('11050505-0505-0505-0505-050505050505', 'اللحوم', 'لَحمُ البَقَر، لَحمُ الغَنَم، دَجاج، سَمَك', 'Laḥmu al-baqar, laḥmu al-ghanam, dajāj, samak', 4),
  ('11050505-0505-0505-0505-050505050505', 'المشروبات الساخنة', 'شايٌ، قَهوَةٌ، حَليبٌ ساخِنٌ', 'Shāyun, qahwatun, ḥalībun sākhin', 5),
  ('11050505-0505-0505-0505-050505050505', 'المشروبات الباردة', 'ماءٌ، عَصيرٌ، لَبَنٌ بارِدٌ', 'Māʾun, ʿaṣīrun, labanun bārid', 6),
  ('11050505-0505-0505-0505-050505050505', 'في المطعم', 'أُريدُ قائِمَةَ الطَّعام مِن فَضلِكَ', 'Urīdu qāʾimata aṭ-ṭaʿām min faḍlika', 7),
  ('11050505-0505-0505-0505-050505050505', 'طلب الطعام', 'أُريدُ طَبَقَ رُزٍّ مَعَ دَجاج', 'Urīdu ṭabaqa ruzzin maʿa dajāj', 8),
  ('11050505-0505-0505-0505-050505050505', 'الطعم واللذة', 'هذا الطَّعامُ لَذيذٌ جِدّاً. شُكراً لَكُم', 'Hādhā aṭ-ṭaʿāmu ladhīdhun jiddan. Shukran lakum', 9),
  ('11050505-0505-0505-0505-050505050505', 'الحلويات', 'كُنافَةٌ، بَقلاوَةٌ، حَلوى، تَمرٌ', 'Kunāfatun, baqlāwa, ḥalwā, tamrun', 10),
  ('11050505-0505-0505-0505-050505050505', 'دفع الحساب', 'الحِسابَ مِن فَضلِكَ. كَم المَجموع؟', 'Al-ḥisāba min faḍlika. Kam al-majmūʿ?', 11),
  ('11050505-0505-0505-0505-050505050505', 'آداب الطعام', 'بِسمِ اللهِ. الحَمدُ للهِ الَّذي أطعَمَنا', 'Bismillāh. Al-ḥamdu lillāhi alladhī aṭʿamanā', 12),
  ('11050505-0505-0505-0505-050505050505', 'الوجبات اليومية', 'الفُطورُ، الغَداءُ، العَشاء', 'Al-fuṭūru, al-ghadāʾu, al-ʿashāʾ', 13),
  ('11050505-0505-0505-0505-050505050505', 'المطبخ العربي', 'الكُسكُسُ، الفَلافِل، الحُمُّص، الفُول', 'Al-kuskusu, al-falāfil, al-ḥummuṣ, al-fūl', 14),
  ('11050505-0505-0505-0505-050505050505', 'الضيافة العربية', 'تَفَضَّل، كُل مِن عِندِنا. أهلاً وَسَهلاً', 'Tafaḍḍal, kul min ʿindinā. Ahlan wa sahlan', 15);
INSERT INTO lessons (unit_id, title, arabic_text, transliteration, order_index) VALUES
  ('11060606-0606-0606-0606-060606060606', 'الألوان الأساسية', 'أحمَرُ، أزرَقُ، أصفَرُ', 'Aḥmaru, azraqu, aṣfaru', 1),
  ('11060606-0606-0606-0606-060606060606', 'ألوان أخرى', 'أخضَرُ، بُرتُقاليٌّ، بَنَفسَجيٌّ', 'Akhḍaru, burtuqāliyyun, banafsajiyyun', 2),
  ('11060606-0606-0606-0606-060606060606', 'الأبيض والأسود', 'أبيَضُ، أسوَدُ، رَماديٌّ', 'Abyaḍu, aswadu, ramādiyyun', 3),
  ('11060606-0606-0606-0606-060606060606', 'ألوان الطبيعة', 'لَونُ السَّماءِ أزرَقٌ. لَونُ العُشبِ أخضَر', 'Lawnu as-samāʾi azraq. Lawnu al-ʿushbi akhḍar', 4),
  ('11060606-0606-0606-0606-060606060606', 'الأشكال الأساسية', 'دائِرَةٌ، مُرَبَّعٌ، مُثَلَّثٌ', 'Dāʾiratun, murabbaʿun, muthallathun', 5),
  ('11060606-0606-0606-0606-060606060606', 'أشكال أخرى', 'مُستَطيلٌ، بَيضاوِيٌّ، نَجمَةٌ', 'Mustaṭīlun, bayḍāwiyyun, najmatun', 6),
  ('11060606-0606-0606-0606-060606060606', 'وصف الأشياء', 'الكُرَةُ دائِرِيَّةٌ. الكِتابُ مُستَطيل', 'Al-kuratu dāʾiriyya. Al-kitābu mustaṭīl', 7),
  ('11060606-0606-0606-0606-060606060606', 'اللون المفضل', 'ما لَونُكَ المُفَضَّل؟ لَوني المُفَضَّلُ أخضَر', 'Mā lawnuka al-mufaḍḍal? Lawniya al-mufaḍḍalu akhḍar', 8),
  ('11060606-0606-0606-0606-060606060606', 'ألوان الملابس', 'قَميصي أبيَضُ وَبَنطالي أسوَد', 'Qamīṣī abyaḍu wa banṭālī aswad', 9),
  ('11060606-0606-0606-0606-060606060606', 'ألوان العلم', 'عَلَمُ مِصرَ أحمَرُ وَأبيَضُ وَأسوَد', 'ʿAlamu Miṣra aḥmaru wa abyaḍu wa aswad', 10),
  ('11060606-0606-0606-0606-060606060606', 'الفاتح والغامق', 'أزرَقُ فاتِحٌ، أزرَقُ غامِقٌ', 'Azraqu fātiḥ, azraqu ghāmiq', 11),
  ('11060606-0606-0606-0606-060606060606', 'الأحجام', 'كَبيرٌ، صَغيرٌ، مُتَوَسِّطٌ', 'Kabīrun, ṣaghīrun, mutawassiṭun', 12),
  ('11060606-0606-0606-0606-060606060606', 'المقارنة بالألوان', 'هذا أغمَقُ مِن ذلِكَ', 'Hādhā aghmagu min dhālika', 13),
  ('11060606-0606-0606-0606-060606060606', 'الألوان في الفن', 'الرَّسّامُ يَستَخدِمُ ألواناً كَثيرَة', 'Ar-rassāmu yastakhdumu alwānan kathīra', 14),
  ('11060606-0606-0606-0606-060606060606', 'قوس قزح', 'قَوسُ قُزَح لَهُ سَبعَةُ ألوان', 'Qawsu quzaḥ lahu sabʿatu alwān', 15);
INSERT INTO lessons (unit_id, title, arabic_text, transliteration, order_index) VALUES
  ('11070707-0707-0707-0707-070707070707', 'أيام الأسبوع (1)', 'السَّبتُ، الأحَدُ، الاثنَين', 'As-sabtu, al-aḥadu, al-ithnayn', 1),
  ('11070707-0707-0707-0707-070707070707', 'أيام الأسبوع (2)', 'الثُّلاثاء، الأربِعاء، الخَميس', 'Ath-thulāthāʾu, al-arbiʿāʾu, al-khamīs', 2),
  ('11070707-0707-0707-0707-070707070707', 'يوم الجمعة', 'الجُمُعَةُ يَومُ عُطلَةٍ وَصَلاة', 'Al-jumʿatu yawmu ʿuṭlatin wa ṣalāh', 3),
  ('11070707-0707-0707-0707-070707070707', 'الشهور الميلادية (1)', 'يَنايِر، فِبرايِر، مارِس', 'Yanāyir, fibrāyir, māris', 4),
  ('11070707-0707-0707-0707-070707070707', 'الشهور الميلادية (2)', 'أبريل، مايو، يونيو', 'Abrīl, māyū, yūniyū', 5),
  ('11070707-0707-0707-0707-070707070707', 'الشهور الميلادية (3)', 'يوليو، أغُسطُس، سِبتَمبِر', 'Yūliyū, ughusṭus, sibtambir', 6),
  ('11070707-0707-0707-0707-070707070707', 'الشهور الميلادية (4)', 'أُكتوبِر، نوفَمبِر، ديسَمبِر', 'Uktūbir, nūfambir, dīsambir', 7),
  ('11070707-0707-0707-0707-070707070707', 'الشهور الهجرية (1)', 'مُحَرَّم، صَفَر، رَبيعُ الأوَّل', 'Muḥarram, ṣafar, rabīʿu al-awwal', 8),
  ('11070707-0707-0707-0707-070707070707', 'الشهور الهجرية (2)', 'رَبيعُ الثاني، جُمادى الأولى، جُمادى الآخِرَة', 'Rabīʿu ath-thānī, jumādā al-ūlā, jumādā al-ākhira', 9),
  ('11070707-0707-0707-0707-070707070707', 'الشهور الهجرية (3)', 'رَجَب، شَعبان، رَمَضان', 'Rajab, shaʿbān, ramaḍān', 10),
  ('11070707-0707-0707-0707-070707070707', 'الشهور الهجرية (4)', 'شَوّال، ذو القِعدَة، ذو الحِجَّة', 'Shawwāl, dhū al-qiʿda, dhū al-ḥijja', 11),
  ('11070707-0707-0707-0707-070707070707', 'الفصول الأربعة', 'الرَّبيعُ، الصَّيفُ، الخَريفُ، الشِّتاء', 'Ar-rabīʿu, aṣ-ṣayfu, al-kharīfu, ash-shitāʾ', 12),
  ('11070707-0707-0707-0707-070707070707', 'اليوم والأمس', 'اليَومُ السَّبتُ، أمسِ كانَ الجُمُعَة', 'Al-yawmu as-sabt, amsi kāna al-jumʿa', 13),
  ('11070707-0707-0707-0707-070707070707', 'الغد وبعد غد', 'غَداً الأحَدُ، وَبَعدَ غَدٍ الاثنَين', 'Ghadan al-aḥadu, wa baʿda ghadin al-ithnayn', 14),
  ('11070707-0707-0707-0707-070707070707', 'التاريخ الكامل', 'اليَومُ الخامِسُ مِن شَهرِ رَمَضان', 'Al-yawmu al-khāmisu min shahri ramaḍān', 15);