-- Unit 8: في السوق (Beginner)
INSERT INTO lessons (unit_id, title, arabic_text, transliteration, order_index) VALUES
('11080808-0808-0808-0808-080808080808', 'مرحباً بك في السوق', 'مرحباً بكَ في السُّوق', 'Marḥaban bika fī as-sūq', 1),
('11080808-0808-0808-0808-080808080808', 'بكم هذا؟', 'بِكَم هذا؟ هذا بعشرةِ دراهم', 'Bikam hādhā? Hādhā bi-ʿasharati darāhim', 2),
('11080808-0808-0808-0808-080808080808', 'هل يمكن تخفيض السعر؟', 'هل يُمكِنُ تخفيضُ السِّعر؟', 'Hal yumkinu takhfīḍu as-siʿr?', 3),
('11080808-0808-0808-0808-080808080808', 'هذا غالٍ جداً', 'هذا غالٍ جداً، أريدُ سعراً أقلّ', 'Hādhā ghālin jiddan, urīdu siʿran aqall', 4),
('11080808-0808-0808-0808-080808080808', 'أريد أن أشتري', 'أريدُ أن أشتريَ هذا المنتج', 'Urīdu an ashtariya hādhā al-muntaj', 5),
('11080808-0808-0808-0808-080808080808', 'هذا رخيص', 'هذا رخيصٌ وجودتُهُ عالية', 'Hādhā rakhīṣun wa jawdatuhu ʿāliya', 6),
('11080808-0808-0808-0808-080808080808', 'أين قسم الخضراوات؟', 'أينَ قِسمُ الخُضراواتِ والفواكه؟', 'Ayna qismu al-khuḍrawāti wal-fawākih?', 7),
('11080808-0808-0808-0808-080808080808', 'كيلو من التفاح', 'أريدُ كيلو من التُّفّاحِ من فضلك', 'Urīdu kīlū min at-tuffāḥi min faḍlik', 8),
('11080808-0808-0808-0808-080808080808', 'هل هذا طازج؟', 'هل هذا طازَجٌ؟ نعم، وصلَ اليوم', 'Hal hādhā ṭāzajun? Naʿam, waṣala al-yawm', 9),
('11080808-0808-0808-0808-080808080808', 'أريد الدفع', 'أريدُ الدَّفعَ، كم المجموع؟', 'Urīdu ad-dafʿa, kam al-majmūʿ?', 10),
('11080808-0808-0808-0808-080808080808', 'هل تقبلون البطاقة؟', 'هل تقبلونَ البطاقةَ أم نقداً فقط؟', 'Hal taqbalūna al-biṭāqata am naqdan faqaṭ?', 11),
('11080808-0808-0808-0808-080808080808', 'أعطني كيساً', 'أعطِني كيساً من فضلك', 'Aʿṭinī kīsan min faḍlik', 12),
('11080808-0808-0808-0808-080808080808', 'هل عندكم عرض؟', 'هل عندكُم عرضٌ أو تخفيضات؟', 'Hal ʿindakum ʿarḍun aw takhfīḍāt?', 13),
('11080808-0808-0808-0808-080808080808', 'الباقي', 'هذا الباقي، شكراً لك', 'Hādhā al-bāqī, shukran lak', 14),
('11080808-0808-0808-0808-080808080808', 'شكراً، مع السلامة', 'شكراً جزيلاً، مع السلامة', 'Shukran jazīlan, maʿa as-salāma', 15),

-- Unit 9: المنزل والأثاث (Beginner)
('11090909-0909-0909-0909-090909090909', 'هذا منزلي', 'هذا منزلي، تفضَّل بالدخول', 'Hādhā manzilī, tafaḍḍal bid-dukhūl', 1),
('11090909-0909-0909-0909-090909090909', 'غرفة الجلوس', 'هذهِ غرفةُ الجلوسِ، اجلِس هنا', 'Hādhihi ghurfatu al-julūsi, ijlis hunā', 2),
('11090909-0909-0909-0909-090909090909', 'غرفة النوم', 'غرفةُ النَّومِ في الطابقِ الثاني', 'Ghurfatu an-nawmi fī aṭ-ṭābiqi ath-thānī', 3),
('11090909-0909-0909-0909-090909090909', 'المطبخ', 'المطبخُ واسعٌ ونظيف', 'Al-maṭbakhu wāsiʿun wa naẓīf', 4),
('11090909-0909-0909-0909-090909090909', 'الحمام', 'الحمَّامُ على اليمين', 'Al-ḥammāmu ʿalā al-yamīn', 5),
('11090909-0909-0909-0909-090909090909', 'الكرسي والطاولة', 'الكُرسيُّ بجانبِ الطاولة', 'Al-kursiyyu bijānibi aṭ-ṭāwila', 6),
('11090909-0909-0909-0909-090909090909', 'السرير', 'السَّريرُ مريحٌ جداً', 'As-sarīru murīḥun jiddan', 7),
('11090909-0909-0909-0909-090909090909', 'الخزانة', 'الخِزانةُ مليئةٌ بالملابس', 'Al-khizānatu malīʾatun bil-malābis', 8),
('11090909-0909-0909-0909-090909090909', 'النافذة والباب', 'افتَح النافذةَ وأغلِق البابَ', 'Iftaḥ an-nāfidhata wa-aghliq al-bāba', 9),
('11090909-0909-0909-0909-090909090909', 'الثلاجة', 'الثلاّجةُ في المطبخ', 'Ath-thallājatu fī al-maṭbakh', 10),
('11090909-0909-0909-0909-090909090909', 'التلفاز', 'التِّلفازُ أمامَ الأريكة', 'At-tilfāzu amāma al-arīka', 11),
('11090909-0909-0909-0909-090909090909', 'المكيف', 'شغِّل المُكيِّفَ، الجوُّ حارّ', 'Shaghghil al-mukayifa, al-jawwu ḥārr', 12),
('11090909-0909-0909-0909-090909090909', 'الحديقة', 'عندَنا حديقةٌ صغيرةٌ خلفَ المنزل', 'ʿIndanā ḥadīqatun ṣaghīratun khalfa al-manzil', 13),
('11090909-0909-0909-0909-090909090909', 'السطح', 'السَّطحُ مكانٌ جميلٌ للجلوس', 'As-saṭḥu makānun jamīlun lil-julūs', 14),
('11090909-0909-0909-0909-090909090909', 'مفتاح المنزل', 'أينَ مفتاحُ المنزل؟', 'Ayna miftāḥu al-manzil?', 15),

-- Unit 10: الصلاة والوضوء (Beginner)
('11101010-1010-1010-1010-101010101010', 'الصلوات الخمس', 'الصَّلواتُ الخمسُ فرضٌ على كلِّ مسلم', 'Aṣ-ṣalawātu al-khamsu farḍun ʿalā kulli muslim', 1),
('11101010-1010-1010-1010-101010101010', 'صلاة الفجر', 'صلاةُ الفَجرِ قبلَ شروقِ الشمس', 'Ṣalātu al-fajri qabla shurūqi ash-shams', 2),
('11101010-1010-1010-1010-101010101010', 'صلاة الظهر', 'صلاةُ الظُّهرِ عندَ زوالِ الشمس', 'Ṣalātu aẓ-ẓuhri ʿinda zawāli ash-shams', 3),
('11101010-1010-1010-1010-101010101010', 'صلاة العصر', 'صلاةُ العَصرِ بعدَ الظُّهر', 'Ṣalātu al-ʿaṣri baʿda aẓ-ẓuhr', 4),
('11101010-1010-1010-1010-101010101010', 'صلاة المغرب', 'صلاةُ المَغرِبِ بعدَ غروبِ الشمس', 'Ṣalātu al-maghribi baʿda ghurūbi ash-shams', 5),
('11101010-1010-1010-1010-101010101010', 'صلاة العشاء', 'صلاةُ العِشاءِ آخرُ صلاةٍ في اليوم', 'Ṣalātu al-ʿishāʾi ākhiru ṣalātin fī al-yawm', 6),
('11101010-1010-1010-1010-101010101010', 'الوضوء', 'الوُضوءُ شرطٌ لصحّةِ الصلاة', 'Al-wuḍūʾu sharṭun li-ṣiḥḥati aṣ-ṣalāh', 7),
('11101010-1010-1010-1010-101010101010', 'غسل اليدين', 'أولاً: اغسِل يديكَ ثلاثَ مرّات', 'Awwalan: ighsil yadayka thalātha marrāt', 8),
('11101010-1010-1010-1010-101010101010', 'المضمضة والاستنشاق', 'ثانياً: تمضمَض واستنشِق', 'Thāniyan: tamaḍmaḍ wastanshiq', 9),
('11101010-1010-1010-1010-101010101010', 'غسل الوجه', 'ثالثاً: اغسِل وجهَك', 'Thālithan: ighsil wajhak', 10),
('11101010-1010-1010-1010-101010101010', 'غسل الذراعين', 'رابعاً: اغسِل ذراعَيك إلى المِرفَقَين', 'Rābiʿan: ighsil dhirāʿayka ilā al-mirfaqayn', 11),
('11101010-1010-1010-1010-101010101010', 'مسح الرأس', 'خامساً: امسَح رأسَك', 'Khāmisan: imsaḥ raʾsak', 12),
('11101010-1010-1010-1010-101010101010', 'غسل القدمين', 'سادساً: اغسِل قدمَيك إلى الكَعبَين', 'Sādisan: ighsil qadamayka ilā al-kaʿbayn', 13),
('11101010-1010-1010-1010-101010101010', 'اتجاه القبلة', 'استقبِل القِبلةَ عندَ الصلاة', 'Istaqbil al-qiblata ʿinda aṣ-ṣalāh', 14),
('11101010-1010-1010-1010-101010101010', 'التسليم', 'السَّلامُ عليكم ورحمةُ الله', 'As-salāmu ʿalaykum wa raḥmatu Allāh', 15);