-- Intermediate Level - Units 1-5
INSERT INTO lessons (unit_id, title, arabic_text, transliteration, order_index) VALUES
-- Unit 1: الأفعال والأزمنة
('22010101-0101-0101-0101-010101010101', 'مقدمة في الأفعال', 'الفِعلُ هو ما دلَّ على حدثٍ مرتبطٍ بزمن', 'Al-fiʿlu huwa mā dalla ʿalā ḥadathin murtabiṭin bi-zaman', 1),
('22010101-0101-0101-0101-010101010101', 'الفعل الماضي', 'الفعلُ الماضي يدلُّ على حدثٍ وقعَ في الزمنِ الماضي', 'Al-fiʿlu al-māḍī yadullu ʿalā ḥadathin waqaʿa fī az-zamani al-māḍī', 2),
('22010101-0101-0101-0101-010101010101', 'أمثلة الفعل الماضي', 'ذهبَ، كتبَ، قرأَ، أكلَ، شربَ', 'Dhahaba, kataba, qaraʾa, akala, shariba', 3),
('22010101-0101-0101-0101-010101010101', 'الفعل المضارع', 'الفعلُ المضارعُ يدلُّ على حدثٍ يقعُ الآن أو سيقع', 'Al-fiʿlu al-muḍāriʿu yadullu ʿalā ḥadathin yaqaʿu al-āna aw sayaqaʿ', 4),
('22010101-0101-0101-0101-010101010101', 'أمثلة الفعل المضارع', 'يذهبُ، يكتبُ، يقرأُ، يأكلُ، يشربُ', 'Yadhhabu, yaktubu, yaqraʾu, yaʾkulu, yashrabu', 5),
('22010101-0101-0101-0101-010101010101', 'فعل الأمر', 'فعلُ الأمرِ يطلبُ حدوثَ شيءٍ في المستقبل', 'Fiʿlu al-amri yaṭlubu ḥudūtha shayʾin fī al-mustaqbal', 6),
('22010101-0101-0101-0101-010101010101', 'أمثلة فعل الأمر', 'اذهَب، اكتُب، اقرَأ، كُل، اشرَب', 'Idh-hab, uktub, iqraʾ, kul, ishrab', 7),
('22010101-0101-0101-0101-010101010101', 'تصريف الفعل الماضي', 'كتبَ، كتبتَ، كتبتِ، كتبتُ، كتبنا', 'Kataba, katabta, katabti, katabtu, katabnā', 8),
('22010101-0101-0101-0101-010101010101', 'تصريف الفعل المضارع', 'يكتبُ، تكتبُ، تكتبينَ، أكتبُ، نكتبُ', 'Yaktubu, taktubu, taktubīna, aktubu, naktubu', 9),
('22010101-0101-0101-0101-010101010101', 'المستقبل بـ سَ', 'سأذهبُ إلى المدرسةِ غداً', 'Sa-adhhabu ilā al-madrasati ghadan', 10),
('22010101-0101-0101-0101-010101010101', 'المستقبل بـ سوف', 'سوفَ أسافرُ الأسبوعَ القادم', 'Sawfa usāfiru al-usbūʿa al-qādim', 11),
('22010101-0101-0101-0101-010101010101', 'النفي بـ لم', 'لم يذهَب إلى العمل أمس', 'Lam yadh-hab ilā al-ʿamal ams', 12),
('22010101-0101-0101-0101-010101010101', 'النفي بـ لا', 'لا أعرفُ الإجابة', 'Lā aʿrifu al-ijāba', 13),
('22010101-0101-0101-0101-010101010101', 'النفي بـ ما', 'ما ذهبتُ إلى السوق', 'Mā dhahabtu ilā as-sūq', 14),
('22010101-0101-0101-0101-010101010101', 'مراجعة الأزمنة', 'ذهبَ (ماضٍ)، يذهبُ (مضارع)، اذهَب (أمر)', 'Dhahaba (māḍin), yadhhabu (muḍāriʿ), idh-hab (amr)', 15),

-- Unit 2: السفر والمطار
('22020202-0202-0202-0202-020202020202', 'في المطار', 'مرحباً بكَ في المطارِ الدَّولي', 'Marḥaban bika fī al-maṭāri ad-duwalī', 1),
('22020202-0202-0202-0202-020202020202', 'تسجيل الوصول', 'أريدُ تسجيلَ الوصولِ لرحلتي', 'Urīdu tasjīla al-wuṣūli li-riḥlatī', 2),
('22020202-0202-0202-0202-020202020202', 'جواز السفر', 'جوازُ سفري وتذكرتي من فضلك', 'Jawāzu safarī wa tadhkaratī min faḍlik', 3),
('22020202-0202-0202-0202-020202020202', 'الأمتعة', 'كم حقيبةً يمكنني حملُها؟', 'Kam ḥaqībatan yumkinunī ḥamluhā?', 4),
('22020202-0202-0202-0202-020202020202', 'بوابة الصعود', 'أينَ بوابةُ الصعودِ رقم خمسة؟', 'Ayna bawwābatu aṣ-ṣuʿūdi raqam khamsa?', 5),
('22020202-0202-0202-0202-020202020202', 'التفتيش الأمني', 'يُرجى المرورُ عبرَ التفتيشِ الأمني', 'Yurjā al-murūru ʿabra at-taftīshi al-amnī', 6),
('22020202-0202-0202-0202-020202020202', 'صالة الانتظار', 'سأنتظرُ في صالةِ الانتظار', 'Sa-antaẓiru fī ṣālati al-intiẓār', 7),
('22020202-0202-0202-0202-020202020202', 'إعلان الصعود', 'يُدعى المسافرون إلى بوابةِ الصعود', 'Yudʿā al-musāfirūna ilā bawwābati aṣ-ṣuʿūd', 8),
('22020202-0202-0202-0202-020202020202', 'على متن الطائرة', 'مقعدي بجانبِ النافذة', 'Maqʿadī bijānibi an-nāfidha', 9),
('22020202-0202-0202-0202-020202020202', 'حزام الأمان', 'يُرجى ربطُ حزامِ الأمان', 'Yurjā rabṭu ḥizāmi al-amān', 10),
('22020202-0202-0202-0202-020202020202', 'الإقلاع', 'الطائرةُ تُقلِعُ الآن', 'Aṭ-ṭāʾiratu tuqliʿu al-ān', 11),
('22020202-0202-0202-0202-020202020202', 'أثناء الرحلة', 'هل يمكنني الحصولُ على ماء؟', 'Hal yumkinunī al-ḥuṣūlu ʿalā māʾ?', 12),
('22020202-0202-0202-0202-020202020202', 'الهبوط', 'استعدّوا للهبوط', 'Istaʿiddū lil-hubūṭ', 13),
('22020202-0202-0202-0202-020202020202', 'استلام الأمتعة', 'أينَ صالةُ استلامِ الأمتعة؟', 'Ayna ṣālatu istilāmi al-amtiʿa?', 14),
('22020202-0202-0202-0202-020202020202', 'الجمارك', 'هل لديكَ شيءٌ للتصريح؟', 'Hal ladayka shayʾun lit-taṣrīḥ?', 15),

-- Unit 3: الجمل الاسمية
('22030303-0303-0303-0303-030303030303', 'تعريف الجملة الاسمية', 'الجملةُ الاسميةُ تبدأُ باسم', 'Al-jumlatu al-ismiyyatu tabdaʾu bi-ism', 1),
('22030303-0303-0303-0303-030303030303', 'المبتدأ', 'المبتدأُ هو الاسمُ الذي نبدأُ به الجملة', 'Al-mubtadaʾu huwa al-ismu alladhī nabdaʾu bihi al-jumla', 2),
('22030303-0303-0303-0303-030303030303', 'الخبر', 'الخبرُ هو ما يُخبِرُ عن المبتدأ', 'Al-khabaru huwa mā yukhbiru ʿan al-mubtadaʾ', 3),
('22030303-0303-0303-0303-030303030303', 'أمثلة بسيطة', 'الطالبُ مجتهدٌ، السماءُ صافيةٌ', 'Aṭ-ṭālibu mujtahidun, as-samāʾu ṣāfiyatun', 4),
('22030303-0303-0303-0303-030303030303', 'إعراب المبتدأ', 'المبتدأُ مرفوعٌ دائماً', 'Al-mubtadaʾu marfūʿun dāʾiman', 5),
('22030303-0303-0303-0303-030303030303', 'إعراب الخبر', 'الخبرُ مرفوعٌ أيضاً', 'Al-khabaru marfūʿun ayḍan', 6),
('22030303-0303-0303-0303-030303030303', 'الخبر المفرد', 'البيتُ كبيرٌ - كبيرٌ خبر مفرد', 'Al-baytu kabīrun - kabīrun khabar mufrad', 7),
('22030303-0303-0303-0303-030303030303', 'الخبر الجملة', 'الطالبُ يدرسُ - يدرس خبر جملة فعلية', 'Aṭ-ṭālibu yadrusu - yadrusu khabar jumla fiʿliyya', 8),
('22030303-0303-0303-0303-030303030303', 'الخبر شبه الجملة', 'الكتابُ على الطاولة - على الطاولة خبر', 'Al-kitābu ʿalā aṭ-ṭāwila - ʿalā aṭ-ṭāwila khabar', 9),
('22030303-0303-0303-0303-030303030303', 'تقديم الخبر', 'في البيتِ رجلٌ - تقدّم الخبر على المبتدأ', 'Fī al-bayti rajulun - taqaddama al-khabar ʿalā al-mubtadaʾ', 10),
('22030303-0303-0303-0303-030303030303', 'المبتدأ المعرفة', 'المبتدأُ يكونُ معرفةً غالباً', 'Al-mubtadaʾu yakūnu maʿrifatan ghāliban', 11),
('22030303-0303-0303-0303-030303030303', 'الخبر النكرة', 'الخبرُ يكونُ نكرةً غالباً', 'Al-khabaru yakūnu nakiratan ghāliban', 12),
('22030303-0303-0303-0303-030303030303', 'حذف المبتدأ', 'كيفَ حالُك؟ - حالُك مبتدأ محذوف', 'Kayfa ḥāluka? - ḥāluka mubtadaʾ maḥdhūf', 13),
('22030303-0303-0303-0303-030303030303', 'حذف الخبر', 'نعم، أنا - الخبر محذوف تقديره موجود', 'Naʿam, ana - al-khabar maḥdhūf taqdīruhu mawjūd', 14),
('22030303-0303-0303-0303-030303030303', 'تطبيقات', 'أعرِب: العلمُ نورٌ', 'Aʿrib: Al-ʿilmu nūrun', 15),

-- Unit 4: في الفندق
('22040404-0404-0404-0404-040404040404', 'الوصول للفندق', 'مساءُ الخيرِ، لديَّ حجزٌ', 'Masāʾu al-khayri, ladayya ḥajzun', 1),
('22040404-0404-0404-0404-040404040404', 'تأكيد الحجز', 'الحجزُ باسمِ أحمد محمد', 'Al-ḥajzu bi-ismi Aḥmad Muḥammad', 2),
('22040404-0404-0404-0404-040404040404', 'نوع الغرفة', 'أريدُ غرفةً مزدوجةً بإطلالةٍ على البحر', 'Urīdu ghurfatan muzdawajatan bi-iṭlālatin ʿalā al-baḥr', 3),
('22040404-0404-0404-0404-040404040404', 'مدة الإقامة', 'سأُقيمُ ثلاثَ ليالٍ', 'Sa-uqīmu thalātha layālin', 4),
('22040404-0404-0404-0404-040404040404', 'الإفطار', 'هل الإفطارُ مشمولٌ في السعر؟', 'Hal al-ifṭāru mashmūlun fī as-siʿr?', 5),
('22040404-0404-0404-0404-040404040404', 'مفتاح الغرفة', 'تفضَّل مفتاحَ غرفتك، رقم ٣٠٥', 'Tafaḍḍal miftāḥa ghurfatik, raqam 305', 6),
('22040404-0404-0404-0404-040404040404', 'المصعد', 'أينَ المصعدُ؟ في نهايةِ الممرّ', 'Ayna al-miṣʿadu? Fī nihāyati al-mamarr', 7),
('22040404-0404-0404-0404-040404040404', 'خدمة الغرف', 'أريدُ طلبَ خدمةِ الغرف', 'Urīdu ṭalaba khidmati al-ghuraf', 8),
('22040404-0404-0404-0404-040404040404', 'التنظيف', 'متى يتمُّ تنظيفُ الغرفة؟', 'Matā yatimmu tanẓīfu al-ghurfa?', 9),
('22040404-0404-0404-0404-040404040404', 'مشكلة في الغرفة', 'التكييفُ لا يعمل', 'At-takyīfu lā yaʿmal', 10),
('22040404-0404-0404-0404-040404040404', 'الواي فاي', 'ما هي كلمةُ سرِّ الواي فاي؟', 'Mā hiya kalimatu sirri al-wāy fāy?', 11),
('22040404-0404-0404-0404-040404040404', 'المسبح', 'أينَ المسبحُ؟ في الطابقِ الأرضي', 'Ayna al-masbaḥu? Fī aṭ-ṭābiqi al-arḍī', 12),
('22040404-0404-0404-0404-040404040404', 'تمديد الإقامة', 'أريدُ تمديدَ إقامتي ليومَين', 'Urīdu tamdīda iqāmatī li-yawmayn', 13),
('22040404-0404-0404-0404-040404040404', 'تسجيل المغادرة', 'أريدُ تسجيلَ المغادرة', 'Urīdu tasjīla al-mughādara', 14),
('22040404-0404-0404-0404-040404040404', 'الفاتورة', 'أريدُ الفاتورةَ النهائية', 'Urīdu al-fātūrata an-nihāʾiyya', 15),

-- Unit 5: الجمل الفعلية
('22050505-0505-0505-0505-050505050505', 'تعريف الجملة الفعلية', 'الجملةُ الفعليةُ تبدأُ بفعل', 'Al-jumlatu al-fiʿliyyatu tabdaʾu bi-fiʿl', 1),
('22050505-0505-0505-0505-050505050505', 'أركان الجملة الفعلية', 'الفعلُ والفاعلُ والمفعول به', 'Al-fiʿlu wal-fāʿilu wal-mafʿūlu bihi', 2),
('22050505-0505-0505-0505-050505050505', 'الفاعل', 'الفاعلُ هو من قامَ بالفعل', 'Al-fāʿilu huwa man qāma bil-fiʿl', 3),
('22050505-0505-0505-0505-050505050505', 'إعراب الفاعل', 'الفاعلُ مرفوعٌ دائماً', 'Al-fāʿilu marfūʿun dāʾiman', 4),
('22050505-0505-0505-0505-050505050505', 'المفعول به', 'المفعولُ به هو من وقعَ عليه الفعل', 'Al-mafʿūlu bihi huwa man waqaʿa ʿalayhi al-fiʿl', 5),
('22050505-0505-0505-0505-050505050505', 'إعراب المفعول به', 'المفعولُ به منصوبٌ دائماً', 'Al-mafʿūlu bihi manṣūbun dāʾiman', 6),
('22050505-0505-0505-0505-050505050505', 'مثال تطبيقي', 'كتبَ الطالبُ الدرسَ', 'Kataba aṭ-ṭālibu ad-darsa', 7),
('22050505-0505-0505-0505-050505050505', 'إعراب المثال', 'كتب: فعل، الطالب: فاعل، الدرس: مفعول به', 'Kataba: fiʿl, aṭ-ṭālib: fāʿil, ad-dars: mafʿūl bih', 8),
('22050505-0505-0505-0505-050505050505', 'الفاعل الضمير', 'ذهبتُ إلى المدرسة - التاء فاعل', 'Dhahabtu ilā al-madrasa - at-tāʾ fāʿil', 9),
('22050505-0505-0505-0505-050505050505', 'جملة بدون مفعول', 'نامَ الطفلُ - جملة فعلية لازمة', 'Nāma aṭ-ṭiflu - jumla fiʿliyya lāzima', 10),
('22050505-0505-0505-0505-050505050505', 'الفعل المتعدي', 'الفعلُ المتعدّي يحتاجُ مفعولاً به', 'Al-fiʿlu al-mutaʿaddī yaḥtāju mafʿūlan bihi', 11),
('22050505-0505-0505-0505-050505050505', 'الفعل اللازم', 'الفعلُ اللازمُ لا يحتاجُ مفعولاً به', 'Al-fiʿlu al-lāzimu lā yaḥtāju mafʿūlan bihi', 12),
('22050505-0505-0505-0505-050505050505', 'نائب الفاعل', 'كُتِبَ الدرسُ - الدرس نائب فاعل', 'Kutiba ad-darsu - ad-dars nāʾib fāʿil', 13),
('22050505-0505-0505-0505-050505050505', 'المبني للمجهول', 'الفعلُ المبنيُّ للمجهول يحتاجُ نائبَ فاعل', 'Al-fiʿlu al-mabniyyu lil-majhūl yaḥtāju nāʾiba fāʿil', 14),
('22050505-0505-0505-0505-050505050505', 'تطبيقات', 'أعرِب: شربَ الولدُ الحليبَ', 'Aʿrib: Shariba al-waladu al-ḥalība', 15);