-- Intermediate Level - Units 6-10
INSERT INTO lessons (unit_id, title, arabic_text, transliteration, order_index) VALUES
-- Unit 6: عند الطبيب
('22060606-0606-0606-0606-060606060606', 'في العيادة', 'مرحباً، لديَّ موعدٌ مع الطبيب', 'Marḥaban, ladayya mawʿidun maʿa aṭ-ṭabīb', 1),
('22060606-0606-0606-0606-060606060606', 'الأعراض', 'أشعرُ بألمٍ في رأسي', 'Ashʿuru bi-alamin fī raʾsī', 2),
('22060606-0606-0606-0606-060606060606', 'وصف الألم', 'الألمُ شديدٌ ومستمرّ', 'Al-alamu shadīdun wa mustamirr', 3),
('22060606-0606-0606-0606-060606060606', 'منذ متى؟', 'منذُ ثلاثةِ أيام', 'Mundhu thalāthati ayyām', 4),
('22060606-0606-0606-0606-060606060606', 'الحرارة', 'لديَّ حرارةٌ مرتفعة', 'Ladayya ḥarāratun murtafiʿa', 5),
('22060606-0606-0606-0606-060606060606', 'الفحص', 'دعني أفحصُك', 'Daʿnī afḥaṣuka', 6),
('22060606-0606-0606-0606-060606060606', 'التشخيص', 'أنتَ مصابٌ بالإنفلونزا', 'Anta muṣābun bil-influwanzā', 7),
('22060606-0606-0606-0606-060606060606', 'الوصفة الطبية', 'سأكتبُ لكَ وصفةً طبية', 'Sa-aktubu laka waṣfatan ṭibbiyya', 8),
('22060606-0606-0606-0606-060606060606', 'الدواء', 'تناوَل هذا الدواءَ ثلاثَ مرّاتٍ يومياً', 'Tanāwal hādhā ad-dawāʾa thalātha marrātin yawmiyyan', 9),
('22060606-0606-0606-0606-060606060606', 'النصائح', 'أكثِر من شربِ السوائل والراحة', 'Akthir min shurbi as-sawāʾil war-rāḥa', 10),
('22060606-0606-0606-0606-060606060606', 'الصيدلية', 'أينَ أقربُ صيدلية؟', 'Ayna aqrabu ṣaydaliyya?', 11),
('22060606-0606-0606-0606-060606060606', 'شراء الدواء', 'أريدُ هذا الدواء من فضلك', 'Urīdu hādhā ad-dawāʾa min faḍlik', 12),
('22060606-0606-0606-0606-060606060606', 'الحساسية', 'هل لديكَ حساسيةٌ من أيِّ دواء؟', 'Hal ladayka ḥasāsiyyatun min ayyi dawāʾ?', 13),
('22060606-0606-0606-0606-060606060606', 'موعد المتابعة', 'عُد بعدَ أسبوع للمتابعة', 'ʿUd baʿda usbūʿ lil-mutābaʿa', 14),
('22060606-0606-0606-0606-060606060606', 'الشفاء', 'أتمنّى لكَ الشفاءَ العاجل', 'Atamannā laka ash-shifāʾa al-ʿājil', 15),

-- Unit 7: النحو الأساسي
('22070707-0707-0707-0707-070707070707', 'علامات الإعراب', 'علاماتُ الإعرابِ أربعٌ: الضمة والفتحة والكسرة والسكون', 'ʿAlāmātu al-iʿrābi arbaʿun: aḍ-ḍamma wal-fatḥa wal-kasra was-sukūn', 1),
('22070707-0707-0707-0707-070707070707', 'الرفع', 'علامةُ الرفعِ الأصليةُ هي الضمة', 'ʿAlāmatu ar-rafʿi al-aṣliyyatu hiya aḍ-ḍamma', 2),
('22070707-0707-0707-0707-070707070707', 'النصب', 'علامةُ النصبِ الأصليةُ هي الفتحة', 'ʿAlāmatu an-naṣbi al-aṣliyyatu hiya al-fatḥa', 3),
('22070707-0707-0707-0707-070707070707', 'الجر', 'علامةُ الجرِّ الأصليةُ هي الكسرة', 'ʿAlāmatu al-jarri al-aṣliyyatu hiya al-kasra', 4),
('22070707-0707-0707-0707-070707070707', 'الجزم', 'علامةُ الجزمِ الأصليةُ هي السكون', 'ʿAlāmatu al-jazmi al-aṣliyyatu hiya as-sukūn', 5),
('22070707-0707-0707-0707-070707070707', 'المرفوعات', 'المرفوعاتُ: المبتدأ والخبر والفاعل ونائب الفاعل', 'Al-marfūʿātu: al-mubtadaʾ wal-khabar wal-fāʿil wa nāʾib al-fāʿil', 6),
('22070707-0707-0707-0707-070707070707', 'المنصوبات', 'المنصوباتُ: المفعول به والحال والتمييز', 'Al-manṣūbātu: al-mafʿūl bihi wal-ḥāl wat-tamyīz', 7),
('22070707-0707-0707-0707-070707070707', 'المجرورات', 'المجروراتُ: ما سبقَه حرفُ جرّ أو مضاف', 'Al-majrūrātu: mā sabaqahu ḥarfu jarrin aw muḍāf', 8),
('22070707-0707-0707-0707-070707070707', 'حروف الجر', 'حروفُ الجرّ: من، إلى، عن، على، في، الباء، اللام، الكاف', 'Ḥurūfu al-jarr: min, ilā, ʿan, ʿalā, fī, al-bāʾ, al-lām, al-kāf', 9),
('22070707-0707-0707-0707-070707070707', 'المضاف والمضاف إليه', 'كتابُ الطالبِ - الطالب مضاف إليه مجرور', 'Kitābu aṭ-ṭālibi - aṭ-ṭālib muḍāf ilayhi majrūr', 10),
('22070707-0707-0707-0707-070707070707', 'الممنوع من الصرف', 'أحمدُ وفاطمةُ ممنوعان من الصرف', 'Aḥmadu wa Fāṭimatu mamnūʿāni min aṣ-ṣarf', 11),
('22070707-0707-0707-0707-070707070707', 'علامات فرعية', 'الألفُ والواوُ والياءُ علاماتٌ فرعية', 'Al-alifu wal-wāwu wal-yāʾu ʿalāmātun farʿiyya', 12),
('22070707-0707-0707-0707-070707070707', 'جمع المذكر السالم', 'المعلمونَ فاعل مرفوع بالواو', 'Al-muʿallimūna fāʿil marfūʿ bil-wāw', 13),
('22070707-0707-0707-0707-070707070707', 'المثنى', 'الطالبانِ مبتدأ مرفوع بالألف', 'Aṭ-ṭālibāni mubtadaʾ marfūʿ bil-alif', 14),
('22070707-0707-0707-0707-070707070707', 'تطبيقات', 'أعرِب: ذهبَ المعلمون إلى المدرسة', 'Aʿrib: Dhahaba al-muʿallimūna ilā al-madrasa', 15),

-- Unit 8: المواصلات
('22080808-0808-0808-0808-080808080808', 'السؤال عن الاتجاهات', 'كيفَ أصلُ إلى المحطة؟', 'Kayfa aṣilu ilā al-maḥaṭṭa?', 1),
('22080808-0808-0808-0808-080808080808', 'يمين ويسار', 'انعطِف يميناً ثمَّ يساراً', 'Inʿaṭif yamīnan thumma yasāran', 2),
('22080808-0808-0808-0808-080808080808', 'مستقيم', 'سِر مستقيماً حتى التقاطع', 'Sir mustaqīman ḥattā at-taqāṭuʿ', 3),
('22080808-0808-0808-0808-080808080808', 'الباص', 'أيُّ باصٍ يذهبُ إلى وسطِ المدينة؟', 'Ayyu bāṣin yadhhabu ilā wasaṭi al-madīna?', 4),
('22080808-0808-0808-0808-080808080808', 'محطة الباص', 'أينَ أقربُ محطةِ باص؟', 'Ayna aqrabu maḥaṭṭati bāṣ?', 5),
('22080808-0808-0808-0808-080808080808', 'التذكرة', 'أريدُ تذكرةً إلى المتحف', 'Urīdu tadhkaratan ilā al-matḥaf', 6),
('22080808-0808-0808-0808-080808080808', 'التاكسي', 'أريدُ تاكسي إلى الفندق', 'Urīdu tāksī ilā al-funduq', 7),
('22080808-0808-0808-0808-080808080808', 'كم المسافة؟', 'كم تبعدُ المحطةُ من هنا؟', 'Kam tabʿudu al-maḥaṭṭatu min hunā?', 8),
('22080808-0808-0808-0808-080808080808', 'كم الوقت؟', 'كم يستغرقُ الوصولُ إلى هناك؟', 'Kam yastaghriqu al-wuṣūlu ilā hunāk?', 9),
('22080808-0808-0808-0808-080808080808', 'القطار', 'متى يغادرُ القطارُ القادم؟', 'Matā yughādiru al-qiṭāru al-qādim?', 10),
('22080808-0808-0808-0808-080808080808', 'تأخر القطار', 'القطارُ متأخرٌ عشرَ دقائق', 'Al-qiṭāru mutaʾakhkhirun ʿashra daqāʾiq', 11),
('22080808-0808-0808-0808-080808080808', 'المترو', 'أيُّ خطِّ مترو يذهبُ إلى المطار؟', 'Ayyu khaṭṭi mitrū yadhhabu ilā al-maṭār?', 12),
('22080808-0808-0808-0808-080808080808', 'تغيير الخط', 'يجبُ أن تغيِّرَ الخطَّ في المحطة التالية', 'Yajibu an tughayyira al-khaṭṭa fī al-maḥaṭṭa at-tāliya', 13),
('22080808-0808-0808-0808-080808080808', 'الوصول', 'وصلنا إلى وجهتِنا', 'Waṣalnā ilā wijhatinā', 14),
('22080808-0808-0808-0808-080808080808', 'شكراً للسائق', 'شكراً جزيلاً، احتفِظ بالباقي', 'Shukran jazīlan, iḥtafiẓ bil-bāqī', 15),

-- Unit 9: القصص القرآني
('22090909-0909-0909-0909-090909090909', 'قصة آدم', 'خلقَ اللهُ آدمَ من طين', 'Khalaqa Allāhu Ādama min ṭīn', 1),
('22090909-0909-0909-0909-090909090909', 'سجود الملائكة', 'أمرَ اللهُ الملائكةَ بالسجودِ لآدم', 'Amara Allāhu al-malāʾikata bis-sujūdi li-Ādam', 2),
('22090909-0909-0909-0909-090909090909', 'إبليس', 'أبى إبليسُ واستكبرَ', 'Abā Iblīsu wastakbara', 3),
('22090909-0909-0909-0909-090909090909', 'قصة نوح', 'دعا نوحٌ قومَهُ تسعَ مئةٍ وخمسينَ عاماً', 'Daʿā Nūḥun qawmahu tisʿa miʾatin wa khamsīna ʿāman', 4),
('22090909-0909-0909-0909-090909090909', 'السفينة', 'بنى نوحٌ السفينةَ بأمرِ الله', 'Banā Nūḥun as-safīnata bi-amri Allāh', 5),
('22090909-0909-0909-0909-090909090909', 'الطوفان', 'جاءَ الطوفانُ وغرقَ الكافرون', 'Jāʾa aṭ-ṭūfānu wa ghariqa al-kāfirūn', 6),
('22090909-0909-0909-0909-090909090909', 'قصة إبراهيم', 'إبراهيمُ أبو الأنبياء', 'Ibrāhīmu abū al-anbiyāʾ', 7),
('22090909-0909-0909-0909-090909090909', 'تحطيم الأصنام', 'حطَّمَ إبراهيمُ الأصنامَ', 'Ḥaṭṭama Ibrāhīmu al-aṣnāma', 8),
('22090909-0909-0909-0909-090909090909', 'النار', 'أُلقيَ إبراهيمُ في النار فكانت برداً وسلاماً', 'Ulqiya Ibrāhīmu fī an-nāri fa-kānat bardan wa salāman', 9),
('22090909-0909-0909-0909-090909090909', 'قصة يوسف', 'يوسفُ أجملُ الناس', 'Yūsufu ajmalu an-nās', 10),
('22090909-0909-0909-0909-090909090909', 'الإخوة والبئر', 'ألقى إخوتُهُ يوسفَ في البئر', 'Alqā ikhwatuhu Yūsufa fī al-biʾr', 11),
('22090909-0909-0909-0909-090909090909', 'في مصر', 'بِيعَ يوسفُ في مصر', 'Bīʿa Yūsufu fī Miṣr', 12),
('22090909-0909-0909-0909-090909090909', 'السجن', 'دخلَ يوسفُ السجنَ ظلماً', 'Dakhala Yūsufu as-sijna ẓulman', 13),
('22090909-0909-0909-0909-090909090909', 'تفسير الأحلام', 'فسَّرَ يوسفُ رؤيا الملك', 'Fassara Yūsufu ruʾyā al-malik', 14),
('22090909-0909-0909-0909-090909090909', 'العزيز', 'أصبحَ يوسفُ عزيزَ مصر', 'Aṣbaḥa Yūsufu ʿazīza Miṣr', 15),

-- Unit 10: الأخبار والإعلام
('22101010-1010-1010-1010-101010101010', 'نشرة الأخبار', 'مرحباً بكم في نشرةِ الأخبار', 'Marḥaban bikum fī nashrati al-akhbār', 1),
('22101010-1010-1010-1010-101010101010', 'خبر عاجل', 'خبرٌ عاجلٌ من العاصمة', 'Khabarun ʿājilun min al-ʿāṣima', 2),
('22101010-1010-1010-1010-101010101010', 'الأخبار المحلية', 'نبدأُ بالأخبارِ المحلية', 'Nabdaʾu bil-akhbāri al-maḥalliyya', 3),
('22101010-1010-1010-1010-101010101010', 'الأخبار الدولية', 'والآنَ مع الأخبارِ الدولية', 'Wal-āna maʿa al-akhbāri ad-duwaliyya', 4),
('22101010-1010-1010-1010-101010101010', 'الطقس', 'نتوقعُ طقساً مشمساً غداً', 'Natawaqqaʿu ṭaqsan mushmisan ghadan', 5),
('22101010-1010-1010-1010-101010101010', 'الاقتصاد', 'ارتفعَ سعرُ النفطِ اليوم', 'Irtafaʿa siʿru an-nafṭi al-yawm', 6),
('22101010-1010-1010-1010-101010101010', 'الرياضة', 'فازَ الفريقُ الوطني بثلاثةِ أهداف', 'Fāza al-farīqu al-waṭaniyyu bi-thalāthati ahdāf', 7),
('22101010-1010-1010-1010-101010101010', 'الصحفي', 'ينقلُ لنا مراسلُنا من الميدان', 'Yanqulu lanā murāsilunā min al-maydān', 8),
('22101010-1010-1010-1010-101010101010', 'المقابلة', 'أجرينا مقابلةً مع الوزير', 'Ajraynā muqābalatan maʿa al-wazīr', 9),
('22101010-1010-1010-1010-101010101010', 'التقرير', 'إليكم هذا التقرير', 'Ilaykum hādhā at-taqrīr', 10),
('22101010-1010-1010-1010-101010101010', 'التصريح', 'صرَّحَ المسؤولُ بأنَّ...', 'Ṣarraḥa al-masʾūlu bi-anna...', 11),
('22101010-1010-1010-1010-101010101010', 'المؤتمر الصحفي', 'عُقدَ مؤتمرٌ صحفيٌّ اليوم', 'ʿUqida muʾtamarun ṣuḥufiyyun al-yawm', 12),
('22101010-1010-1010-1010-101010101010', 'وسائل التواصل', 'انتشرَ الخبرُ على وسائلِ التواصل', 'Intashara al-khabaru ʿalā wasāʾili at-tawāṣul', 13),
('22101010-1010-1010-1010-101010101010', 'التعليق', 'ما رأيُكَ في هذا الخبر؟', 'Mā raʾyuka fī hādhā al-khabar?', 14),
('22101010-1010-1010-1010-101010101010', 'ختام النشرة', 'إلى هنا نصلُ إلى نهايةِ نشرتِنا', 'Ilā hunā naṣilu ilā nihāyati nashratinā', 15);