-- Advanced Level - Units 1-5
INSERT INTO lessons (unit_id, title, arabic_text, transliteration, order_index) VALUES
-- Unit 1: البلاغة والأدب
('33010101-0101-0101-0101-010101010101', 'مقدمة في البلاغة', 'البلاغةُ هي مطابقةُ الكلامِ لمقتضى الحال', 'Al-balāghatu hiya muṭābaqatu al-kalāmi li-muqtaḍā al-ḥāl', 1),
('33010101-0101-0101-0101-010101010101', 'علم البيان', 'علمُ البيانِ يدرسُ التشبيهَ والاستعارةَ والكناية', 'ʿIlmu al-bayāni yadrusu at-tashbīha wal-istiʿārata wal-kināya', 2),
('33010101-0101-0101-0101-010101010101', 'التشبيه', 'التشبيهُ مقارنةٌ بين شيئين لوجودِ صفةٍ مشتركة', 'At-tashbīhu muqāranatun bayna shayʾayni li-wujūdi ṣifatin mushtaraka', 3),
('33010101-0101-0101-0101-010101010101', 'أركان التشبيه', 'المشبَّهُ والمشبَّهُ به وأداةُ التشبيه ووجهُ الشبه', 'Al-mushabbahu wal-mushabbahu bihi wa adātu at-tashbīh wa wajhu ash-shabah', 4),
('33010101-0101-0101-0101-010101010101', 'الاستعارة', 'الاستعارةُ تشبيهٌ حُذفَ أحدُ طرفَيه', 'Al-istiʿāratu tashbīhun ḥudhifa aḥadu ṭarafayhi', 5),
('33010101-0101-0101-0101-010101010101', 'أنواع الاستعارة', 'استعارةٌ مكنيةٌ واستعارةٌ تصريحية', 'Istiʿāratun makniyyatun wa istiʿāratun taṣrīḥiyya', 6),
('33010101-0101-0101-0101-010101010101', 'الكناية', 'الكنايةُ لفظٌ يُرادُ به غيرُ معناه الأصلي', 'Al-kināyatu lafẓun yurādu bihi ghayru maʿnāhu al-aṣlī', 7),
('33010101-0101-0101-0101-010101010101', 'علم المعاني', 'علمُ المعاني يدرسُ أحوالَ اللفظ العربي', 'ʿIlmu al-maʿānī yadrusu aḥwāla al-lafẓi al-ʿarabī', 8),
('33010101-0101-0101-0101-010101010101', 'الخبر والإنشاء', 'الكلامُ إما خبرٌ أو إنشاء', 'Al-kalāmu immā khabarun aw inshāʾ', 9),
('33010101-0101-0101-0101-010101010101', 'التقديم والتأخير', 'التقديمُ والتأخيرُ لأغراضٍ بلاغية', 'At-taqdīmu wat-taʾkhīru li-aghrāḍin balāghiyya', 10),
('33010101-0101-0101-0101-010101010101', 'علم البديع', 'علمُ البديعِ يدرسُ المحسّناتِ اللفظيةَ والمعنوية', 'ʿIlmu al-badīʿi yadrusu al-muḥassinātil-lafẓiyyata wal-maʿnawiyya', 11),
('33010101-0101-0101-0101-010101010101', 'الجناس', 'الجناسُ تشابهُ لفظَين في النطق', 'Al-jināsu tashābuhu lafẓayni fī an-nuṭq', 12),
('33010101-0101-0101-0101-010101010101', 'الطباق', 'الطباقُ الجمعُ بين المتضادَّين', 'Aṭ-ṭibāqu al-jamʿu bayna al-mutaḍāddayn', 13),
('33010101-0101-0101-0101-010101010101', 'السجع', 'السجعُ توافقُ الفواصلِ في الكلام', 'As-sajʿu tawāfuqu al-fawāṣili fī al-kalām', 14),
('33010101-0101-0101-0101-010101010101', 'تطبيقات بلاغية', 'حلِّل: وجهُها كالبدرِ في الحُسن', 'Ḥallil: Wajhuhā kal-badri fī al-ḥusn', 15),

-- Unit 2: الأعمال والتجارة
('33020202-0202-0202-0202-020202020202', 'الاجتماع', 'سنعقدُ اجتماعاً غداً الساعةَ العاشرة', 'Sanaʿqidu ijtimāʿan ghadan as-sāʿata al-ʿāshira', 1),
('33020202-0202-0202-0202-020202020202', 'جدول الأعمال', 'إليكم جدولَ أعمالِ الاجتماع', 'Ilaykum jadwala aʿmāli al-ijtimāʿ', 2),
('33020202-0202-0202-0202-020202020202', 'العرض التقديمي', 'سأقدِّمُ عرضاً عن المشروعِ الجديد', 'Sa-uqaddimu ʿarḍan ʿan al-mashrūʿi al-jadīd', 3),
('33020202-0202-0202-0202-020202020202', 'التفاوض', 'نحنُ في مرحلةِ التفاوضِ على العقد', 'Naḥnu fī marḥalati at-tafāwuḍi ʿalā al-ʿaqd', 4),
('33020202-0202-0202-0202-020202020202', 'الشروط', 'ما هي شروطُكم للتعاقد؟', 'Mā hiya shurūṭukum lit-taʿāqud?', 5),
('33020202-0202-0202-0202-020202020202', 'العقد', 'وقَّعنا عقدَ الشراكة', 'Waqqaʿnā ʿaqda ash-sharāka', 6),
('33020202-0202-0202-0202-020202020202', 'الميزانية', 'الميزانيةُ المخصصةُ للمشروع', 'Al-mīzāniyyatu al-mukhaṣṣaṣatu lil-mashrūʿ', 7),
('33020202-0202-0202-0202-020202020202', 'الأرباح والخسائر', 'حققنا أرباحاً جيدةً هذا العام', 'Ḥaqqaqnā arbāḥan jayyidatan hādhā al-ʿām', 8),
('33020202-0202-0202-0202-020202020202', 'التقرير المالي', 'أقدِّمُ لكم التقريرَ الماليَّ الربعي', 'Uqaddimu lakum at-taqrīra al-māliyya ar-rubʿī', 9),
('33020202-0202-0202-0202-020202020202', 'الاستثمار', 'نبحثُ عن مستثمرينَ جُدُد', 'Nabḥathu ʿan mustathmirīna judud', 10),
('33020202-0202-0202-0202-020202020202', 'الشراكة', 'نقترحُ شراكةً استراتيجية', 'Naqtariḥu sharākatan istrātījiyya', 11),
('33020202-0202-0202-0202-020202020202', 'المنافسة', 'السوقُ تنافسيٌّ جداً', 'As-sūqu tanāfusiyyun jiddan', 12),
('33020202-0202-0202-0202-020202020202', 'التسويق', 'نحتاجُ خطةً تسويقيةً جديدة', 'Naḥtāju khuṭṭatan taswīqiyyatan jadīda', 13),
('33020202-0202-0202-0202-020202020202', 'خدمة العملاء', 'رضا العملاءِ أولويتُنا', 'Riḍā al-ʿumalāʾi awlawiyyatunā', 14),
('33020202-0202-0202-0202-020202020202', 'الإغلاق', 'أتطلَّعُ إلى تعاونٍ مثمر', 'Ataṭallaʿu ilā taʿāwunin muthmir', 15),

-- Unit 3: الإعراب المتقدم
('33030303-0303-0303-0303-030303030303', 'التوابع', 'التوابعُ أربعة: النعت والتوكيد والبدل والعطف', 'At-tawābiʿu arbaʿa: an-naʿt wat-tawkīd wal-badal wal-ʿaṭf', 1),
('33030303-0303-0303-0303-030303030303', 'النعت', 'النعتُ يتبعُ المنعوتَ في الإعراب', 'An-naʿtu yatbaʿu al-manʿūta fī al-iʿrāb', 2),
('33030303-0303-0303-0303-030303030303', 'أنواع النعت', 'النعتُ الحقيقيُّ والنعتُ السببي', 'An-naʿtu al-ḥaqīqiyyu wan-naʿtu as-sababī', 3),
('33030303-0303-0303-0303-030303030303', 'التوكيد', 'التوكيدُ اللفظيُّ والتوكيدُ المعنوي', 'At-tawkīdu al-lafẓiyyu wat-tawkīdu al-maʿnawī', 4),
('33030303-0303-0303-0303-030303030303', 'ألفاظ التوكيد', 'نفس وعين وكلّ وجميع', 'Nafs wa ʿayn wa kull wa jamīʿ', 5),
('33030303-0303-0303-0303-030303030303', 'البدل', 'البدلُ يحلُّ محلَّ المبدلِ منه', 'Al-badalu yaḥullu maḥalla al-mubdali minhu', 6),
('33030303-0303-0303-0303-030303030303', 'أنواع البدل', 'بدلُ كلٍّ من كلّ، بدلُ بعضٍ من كلّ، بدلُ اشتمال', 'Badalu kullin min kull, badalu baʿḍin min kull, badalu ishtimāl', 7),
('33030303-0303-0303-0303-030303030303', 'العطف', 'العطفُ ربطٌ بين المعطوفِ والمعطوفِ عليه', 'Al-ʿaṭfu rabṭun bayna al-maʿṭūfi wal-maʿṭūfi ʿalayhi', 8),
('33030303-0303-0303-0303-030303030303', 'حروف العطف', 'الواو والفاء وثمَّ وأو وبل ولا', 'Al-wāw wal-fāʾ wa thumma wa aw wa bal wa lā', 9),
('33030303-0303-0303-0303-030303030303', 'المنصوبات', 'المفعولُ به والمفعولُ المطلق والمفعولُ لأجله', 'Al-mafʿūlu bihi wal-mafʿūlu al-muṭlaq wal-mafʿūlu li-ajlihi', 10),
('33030303-0303-0303-0303-030303030303', 'الحال', 'الحالُ وصفٌ منصوبٌ يبيِّنُ هيئةَ صاحبِه', 'Al-ḥālu waṣfun manṣūbun yubayyinu hayʾata ṣāḥibihi', 11),
('33030303-0303-0303-0303-030303030303', 'التمييز', 'التمييزُ اسمٌ منصوبٌ يزيلُ الإبهام', 'At-tamyīzu ismun manṣūbun yuzīlu al-ibhām', 12),
('33030303-0303-0303-0303-030303030303', 'الاستثناء', 'الاستثناءُ إخراجُ ما بعدَ إلاّ', 'Al-istīthnāʾu ikhrāju mā baʿda illā', 13),
('33030303-0303-0303-0303-030303030303', 'المنادى', 'المنادى ما يُطلَبُ إقبالُه بحرفٍ من حروفِ النداء', 'Al-munādā mā yuṭlabu iqbāluhu bi-ḥarfin min ḥurūfi an-nidāʾ', 14),
('33030303-0303-0303-0303-030303030303', 'تطبيقات', 'أعرِب: جاءَ الطالبُ المجتهدُ نفسُه', 'Aʿrib: Jāʾa aṭ-ṭālibu al-mujtahidu nafsuhu', 15),

-- Unit 4: السياسة والحكومة
('33040404-0404-0404-0404-040404040404', 'النظام السياسي', 'الدولةُ نظامٌ ملكيٌّ دستوري', 'Ad-dawlatu niẓāmun malakiyyun dustūrī', 1),
('33040404-0404-0404-0404-040404040404', 'الديمقراطية', 'الديمقراطيةُ حكمُ الشعبِ لنفسِه', 'Ad-dīmuqrāṭiyyatu ḥukmu ash-shaʿbi li-nafsihi', 2),
('33040404-0404-0404-0404-040404040404', 'الانتخابات', 'ستُجرى الانتخاباتُ الشهرَ القادم', 'Sa-tujrā al-intikhābātu ash-shahra al-qādim', 3),
('33040404-0404-0404-0404-040404040404', 'التصويت', 'صوَّتَ المواطنون لاختيارِ ممثِّليهم', 'Ṣawwata al-muwāṭinūna li-ikhtiyāri mumaththilīhim', 4),
('33040404-0404-0404-0404-040404040404', 'البرلمان', 'يناقشُ البرلمانُ قوانينَ جديدة', 'Yunāqishu al-barlamānu qawānīna jadīda', 5),
('33040404-0404-0404-0404-040404040404', 'مجلس الوزراء', 'عُيِّنَ مجلسُ الوزراءِ الجديد', 'ʿUyyina majlisu al-wuzarāʾi al-jadīd', 6),
('33040404-0404-0404-0404-040404040404', 'السياسة الخارجية', 'وقَّعتِ الدولتان اتفاقيةً دبلوماسية', 'Waqqaʿati ad-dawlatāni ittifāqiyyatan diblumāsiyya', 7),
('33040404-0404-0404-0404-040404040404', 'حقوق الإنسان', 'حمايةُ حقوقِ الإنسانِ واجبٌ', 'Ḥimāyatu ḥuqūqi al-insāni wājib', 8),
('33040404-0404-0404-0404-040404040404', 'القانون', 'يسري القانونُ على الجميع', 'Yasrī al-qānūnu ʿalā al-jamīʿ', 9),
('33040404-0404-0404-0404-040404040404', 'الدستور', 'الدستورُ أعلى وثيقةٍ قانونية', 'Ad-dustūru aʿlā wathīqatin qānūniyya', 10),
('33040404-0404-0404-0404-040404040404', 'القضاء', 'القضاءُ مستقلٌّ ومحايد', 'Al-qaḍāʾu mustaqillun wa muḥāyid', 11),
('33040404-0404-0404-0404-040404040404', 'الأحزاب', 'تتنافسُ الأحزابُ في الانتخابات', 'Tatanāfasu al-aḥzābu fī al-intikhābāt', 12),
('33040404-0404-0404-0404-040404040404', 'الإصلاح', 'تسعى الحكومةُ للإصلاحِ الاقتصادي', 'Tasʿā al-ḥukūmatu lil-iṣlāḥi al-iqtiṣādī', 13),
('33040404-0404-0404-0404-040404040404', 'الرأي العام', 'يتأثَّرُ القرارُ بالرأيِ العام', 'Yataʾaththaru al-qarāru bir-raʾyi al-ʿāmm', 14),
('33040404-0404-0404-0404-040404040404', 'المواطنة', 'المواطنةُ حقوقٌ وواجبات', 'Al-muwāṭanatu ḥuqūqun wa wājibāt', 15),

-- Unit 5: الكتابة الرسمية
('33050505-0505-0505-0505-050505050505', 'عناصر الرسالة الرسمية', 'التاريخُ والمرسَلُ إليه والموضوعُ والمحتوى والختام', 'At-tārīkhu wal-mursalu ilayhi wal-mawḍūʿu wal-muḥtawā wal-khitām', 1),
('33050505-0505-0505-0505-050505050505', 'البداية', 'سعادةَ المديرِ العام المحترم', 'Saʿādata al-mudīri al-ʿāmmi al-muḥtaram', 2),
('33050505-0505-0505-0505-050505050505', 'التحية', 'تحيةً طيبةً وبعد', 'Taḥiyyatan ṭayyibatan wa baʿd', 3),
('33050505-0505-0505-0505-050505050505', 'الموضوع', 'الموضوع: طلبُ إجازةٍ سنوية', 'Al-mawḍūʿ: Ṭalabu ijāzatin sanawiyya', 4),
('33050505-0505-0505-0505-050505050505', 'المقدمة', 'أتقدَّمُ إلى سيادتِكم بهذا الطلب', 'Ataqaddamu ilā siyādatikum bi-hādhā aṭ-ṭalab', 5),
('33050505-0505-0505-0505-050505050505', 'صلب الموضوع', 'أرجو التكرُّمَ بالموافقةِ على...', 'Arjū at-takarruma bil-muwāfaqati ʿalā...', 6),
('33050505-0505-0505-0505-050505050505', 'التعليل', 'وذلك لأسبابٍ عائلية ملحّة', 'Wa dhālika li-asbābin ʿāʾiliyyatin muliḥḥa', 7),
('33050505-0505-0505-0505-050505050505', 'الشكر المقدم', 'شاكراً لكم حُسنَ تعاونِكم', 'Shākiran lakum ḥusna taʿāwunikum', 8),
('33050505-0505-0505-0505-050505050505', 'الختام', 'وتفضَّلوا بقبولِ فائقِ الاحترام', 'Wa tafaḍḍalū bi-qabūli fāʾiqi al-iḥtirām', 9),
('33050505-0505-0505-0505-050505050505', 'التوقيع', 'مقدِّمُه: الاسمُ والتوقيع', 'Muqaddimuhu: al-ismu wat-tawqīʿ', 10),
('33050505-0505-0505-0505-050505050505', 'التقرير', 'بناءً على طلبِكم، أرفعُ إليكم هذا التقرير', 'Bināʾan ʿalā ṭalabikum, arfaʿu ilaykum hādhā at-taqrīr', 11),
('33050505-0505-0505-0505-050505050505', 'المحضر', 'محضرُ اجتماعِ مجلسِ الإدارة', 'Maḥḍaru ijtimāʿi majlisi al-idāra', 12),
('33050505-0505-0505-0505-050505050505', 'الإشعار الرسمي', 'يُرجى العلمُ بأنَّ...', 'Yurjā al-ʿilmu bi-anna...', 13),
('33050505-0505-0505-0505-050505050505', 'الاستقالة', 'أتقدَّمُ باستقالتي من منصبي', 'Ataqaddamu bi-istiqālatī min manṣibī', 14),
('33050505-0505-0505-0505-050505050505', 'الشكوى الرسمية', 'أتقدَّمُ بشكوى ضدَّ...', 'Ataqaddamu bi-shakwā ḍidda...', 15);